import{j as n,Z as t,a2 as r,u as s,S as o}from"./index-1fdd0a17.js";import{r as p}from"./render-9a41fb79.js";import{T as u}from"./index-f5bce3a1.js";const d="/assets/btn_dv-6fba1bfc.jpg",v=[{label:"Bài tập tùy chỉnh",content:n.jsxs("div",{children:[n.jsx(t.Title,{style:{textTransform:"uppercase"},level:3,children:"bài tập tùy chỉnh"}),n.jsx(t.Paragraph,{children:"Điều gì hiện lên trong đầu bạn khi bạn nghĩ đến việc tập thể dục? Đặc biệt, bạn cần làm gì để tăng chiều cao? Không ngần ngại, tôi liệt kê các môn thể thao như bóng rổ, nhảy dây, bơi lội và taekwondo. Giống như ngoại hình của mỗi cá nhân là khác nhau, thành tích thể thao của mỗi người cũng khác nhau. Do đó, hiệu suất tập thể dục phải được đo lường và đánh giá thông qua bài kiểm tra khả năng tập thể dục nhịp điệu, sau đó bài tập tùy chỉnh phải được thực hiện theo đơn tập thể dục phù hợp với từng cá nhân."})]})},{label:"Chỉnh sửa tư thế",content:n.jsxs("div",{children:[n.jsx(t.Title,{style:{textTransform:"uppercase"},level:3,children:"Chỉnh sửa tư thế"}),n.jsx(t.Paragraph,{children:"Tư thế xấu thường được cho là kết quả của tình trạng lệch xương. Tuy nhiên, điều này cũng phá vỡ sự cân bằng của sức mạnh cơ hỗ trợ xương, dẫn đến tư thế không tốt. Việc đo lường và phân tích chi tiết từ lòng bàn chân đến đỉnh đầu được ưu tiên. Ngoài ra, thói quen sinh hoạt đúng đắn và sự trợ giúp của chuyên gia là hoàn toàn cần thiết để duy trì tư thế đúng . Thiết bị đặc biệt của Kines dùng để điều chỉnh tư thế là lực kéo khi đi bộ."})]})},{label:"Phục hồi chức năng cơ bắp",content:n.jsxs("div",{children:[n.jsx(t.Title,{style:{textTransform:"uppercase"},level:3,children:"phục hồi chức năng cơ bắp"}),n.jsx(t.Paragraph,{children:"Tăng trưởng chiều cao đề cập đến sự phát triển của chân và vòng eo. Tuy nhiên, nếu các cơ nâng đỡ xương chân và lưng yếu thì chúng không thể phát triển tốt. Để so sánh với việc xây dựng một tòa nhà, xương là những thanh thép và cơ bắp là xi măng bao quanh các thanh thép. Chính cơ bắp chứ không phải xương mới tạo nên một cơ thể cân đối và đẹp đẽ. Thanh thiếu niên lùn không có nhiều thời gian để phát triển nên cần có thiết bị khoa học đặc biệt để cải thiện chức năng cơ bắp. Biosys, một thiết bị Kines đặc biệt dùng để cải thiện chức năng cơ bắp"})]})},{label:"Tập thể dục dãn xương",content:n.jsxs("div",{children:[n.jsx(t.Title,{style:{textTransform:"uppercase"},level:3,children:"Tập thể dục dãn xương"}),n.jsx(t.Paragraph,{children:"Bài tập Longman là một bài tập tuyệt vời để tăng tính linh hoạt bằng cách làm mềm cơ và mở rộng phạm vi chuyển động của khớp . Cơ bắp cứng khiến cơ thể căng thẳng và tiết ra cortisol, một loại hormone gây căng thẳng, làm giảm sự phát triển của hormone tăng trưởng khoảng 30% và cản trở giấc ngủ ngon (31) . Tính linh hoạt cao làm tăng phạm vi co và thư giãn cơ, giúp giảm căng thẳng bằng cách giảm căng thẳng không cần thiết. Và khi độ đàn hồi của cơ tăng lên, lưu thông máu được cải thiện, tối đa hóa hiệu quả tập luyện."})]})},{label:"Dovision",content:n.jsxs("div",{children:[n.jsx(t.Title,{style:{textTransform:"uppercase"},level:3,children:"Dovision"}),n.jsx(r,{src:d,preview:!1,wrapperStyle:{width:"100%"}})]})},{label:"Phản hồi thần kinh",content:n.jsxs("div",{children:[n.jsx(t.Title,{style:{textTransform:"uppercase"},level:3,children:"Phản hồi thần kinh"}),n.jsx(t.Paragraph,{children:"Tất cả các hoạt động tinh thần và thể chất của con người đều được thực hiện thông qua hoạt động của não. Không chỉ học tập, ngay cả việc di chuyển một ngón tay cũng liên quan đến việc gửi tín hiệu từ não đến các cơ thông qua các dây thần kinh vận động, khiến các cơ cử động và ngón tay cũng cử động. Do đó, nhiều yếu tố như sức khỏe và sự phát triển thể chất của trẻ, chẳng hạn như tập thể dục, dinh dưỡng, giấc ngủ, ô nhiễm kim loại nặng, mối quan hệ với cha mẹ và thói quen sinh hoạt, đều chịu trách nhiệm cho sự phát triển của mạng lưới thần kinh não."})]})},{label:"Quản lý lối sống",content:n.jsxs("div",{children:[n.jsx(t.Title,{style:{textTransform:"uppercase"},level:3,children:"Quản lý lối sống"}),n.jsx(t.Paragraph,{children:"Bài tập tùy chỉnh kiểm tra tình trạng sức khỏe và mức độ thể lực của một cá nhân thông qua kiểm tra tập thể dục khoa học, xác định loại và cường độ tập thể dục, thời gian tập thể dục và phương pháp tập thể dục phù hợp với tình trạng thể chất của người đó và xác định những rủi ro có thể xảy ra trong quá trình tập luyện . đến bài tập tăng cường sức khỏe được thiết kế để tối đa hóa hiệu quả đồng thời giảm số lượng bài tập . Do đó, việc kết hợp các bài tập thể dục tùy chỉnh vào thói quen hàng ngày đóng vai trò rất quan trọng trong việc phục hồi các chức năng thể chất bị suy yếu, cải thiện sức khỏe,"})]})}],m="_container_1t09m_1",b="_title_1t09m_4",x="_title-divider_1t09m_8",y="_title-sub_1t09m_13",h={container:m,title:b,"title-divider":"_title-divider_1t09m_8",titleDivider:x,"title-sub":"_title-sub_1t09m_13",titleSub:y};function _(){const{t:c}=s(),e=p(c),g=()=>n.jsxs("div",{className:h.container,children:[n.jsx(t.Title,{level:2,className:h.title,children:"Chương trình Kines là chương trình tăng trưởng chiều cao khoa học và có hệ thống."}),n.jsx("div",{className:h.titleDivider}),n.jsx(t.Title,{level:4,className:h.titleSub}),n.jsxs("div",{style:{textAlign:"justify"},children:[n.jsxs(t.Paragraph,{children:["Nó bao gồm"," ",n.jsx(t.Text,{strong:!0,children:"7 chương trình: bài tập tùy chỉnh, điều chỉnh tư thế, phục hồi chức năng cơ, bài tập Longman, phản hồi thần kinh , Dovision và cải thiện lối sống."})]}),n.jsx(t.Paragraph,{children:"Chúng tôi sẽ tìm ra nguyên nhân khiến con bạn thấp bé, đưa ra các đề xuất cải thiện cho từng cá nhân và cung cấp hướng dẫn và quản lý 1:1 từ chuyên gia tại chi nhánh gần đó."}),n.jsx(u,{defaultActiveKey:"1",size:"small",type:"card",items:v.map((i,a)=>{const l=String(a+1);return{label:i.label,key:l,children:i.content}})})]})]});return n.jsx(o,{children:g(),items:e})}export{_ as default};
