import{u as e,j as n,S as u,Z as c}from"./index-1fdd0a17.js";import{r as g}from"./render-dea67213.js";const r="_container_6idc5_1",a="_title_6idc5_1",o="_content_6idc5_6",i={container:r,title:a,content:o};function y(){const{t}=e(),h=g(t),s=()=>n.jsx("div",{className:i.container,children:n.jsxs("div",{children:[n.jsx(c.Title,{level:2,className:i.title,children:"Chương 1: Quy định chung"}),n.jsxs("div",{children:[n.jsx("div",{children:n.jsx(c.Text,{strong:!0,children:"Điều 1 (Mục đích)"})}),n.jsx(c.Text,{className:i.content,children:"Các Điều khoản sử dụng này (sau đây gọi là “Điều khoản và Điều kiện”) chi phối các điều kiện đăng ký và sử dụng dịch vụ Kiness (tên tiếng Hàn: Kines Development Center, sau đây gọi là Dịch vụ) do Công ty cung cấp giữa KINESS (sau đây gọi là Công ty) và khách hàng của Công ty (sau đây gọi là “Thành viên”). Mục đích là quy định cụ thể mọi vấn đề liên quan và các vấn đề cần thiết khác."})]}),n.jsxs("div",{style:{marginTop:20},children:[n.jsx("div",{children:n.jsx(c.Text,{strong:!0,children:"Điều 2 (Hiệu lực và thay đổi Điều khoản sử dụng)"})}),n.jsx(c.Text,{className:i.content,children:"(1) Các điều khoản và điều kiện này có hiệu lực khi được công bố trực tuyến trên trang web của Trung tâm Tăng trưởng Kiness (www.kiness.co.kr, sau đây gọi là 'Trang web của Trung tâm Tăng trưởng Kiness') và nếu có căn cứ hợp lý thì chúng sẽ không vi phạm luật pháp và quy định có liên quan. Nó có thể được sửa đổi trong phạm vi. Các điều khoản và điều kiện sửa đổi sẽ có hiệu lực khi được công bố trực tuyến và các sửa đổi đối với các quy định quan trọng, chẳng hạn như quyền hoặc nghĩa vụ của người dùng, sẽ được thông báo trước."}),n.jsx(c.Text,{className:i.content,children:"(2) Công ty có thể thay đổi các điều khoản và điều kiện này nếu có căn cứ hợp lý và nếu các điều khoản và điều kiện thay đổi, chúng sẽ được thông báo trước ngay lập tức."}),n.jsx(c.Text,{className:i.content,children:"(3) Đồng ý với các Điều khoản và Điều kiện này có nghĩa là bạn đồng ý thường xuyên truy cập trang web để kiểm tra những thay đổi trong Điều khoản và Điều kiện. Công ty không chịu trách nhiệm cho bất kỳ thiệt hại nào mà người dùng phải gánh chịu do thiếu thông tin về các điều khoản và điều kiện đã thay đổi."}),n.jsx(c.Text,{className:i.content,children:"(4) Nếu thành viên không đồng ý với các điều khoản và điều kiện đã thay đổi thì có thể yêu cầu rút (hủy) tư cách thành viên, nếu tiếp tục sử dụng dịch vụ mà không thể hiện ý định từ chối kể cả sau 7 ngày kể từ ngày ngày có hiệu lực của các điều khoản và điều kiện được thay đổi, anh ấy/cô ấy đồng ý với những thay đổi trong các điều khoản và điều kiện đó."})]})]})});return n.jsx(u,{children:s(),items:h})}export{y as default};
